										<AccountId>" + Model.AccountId + "</AccountId>" + @"
										<CampaignId>" + Model.CampaignId + "</CampaignId>" + @"
										<AdGroupId>" + Model.AdGroupId + "</AdGroupId>" + @"
									</AdGroupReportScope>
								</AdGroups>";
			}

			string GeneratePoll() {
				return @"
           <s:Envelope xmlns:s=""http://schemas.xmlsoap.org/soap/envelope/"">
            	<s:Header>
                <h:ApplicationToken i:nil=""true"" xmlns:h=""https://bingads.microsoft.com/Reporting/v13"" xmlns:i=""http://www.w3.org/2001/XMLSchema-instance""/>
                <h:AuthenticationToken xmlns:h=""https://bingads.microsoft.com/Reporting/v13"">" + Model.Authenticator.Token + "</h:AuthenticationToken>" + @"
                <h:CustomerAccountId i:nil=""true""	xmlns:h=""https://bingads.microsoft.com/Reporting/v13""	xmlns:i=""http://www.w3.org/2001/XMLSchema-instance""/>
                <h:CustomerId i:nil=""true"" xmlns:h=""https://bingads.microsoft.com/Reporting/v13"" xmlns:i=""http://www.w3.org/2001/XMLSchema-instance""/>
                <h:DeveloperToken	xmlns:h=""https://bingads.microsoft.com/Reporting/v13"">1193759878590095</h:DeveloperToken>
				    </s:Header>
            <s:Body>
              <PollGenerateReportRequest xmlns=""https://bingads.microsoft.com/Reporting/v13"">
                <ReportRequestId>" + Model.ReportId + "</ReportRequestId>" + @"
              </PollGenerateReportRequest>
            </s:Body>
          </s:Envelope>";
			}

